import React, { useState } from 'react';
import { AppBar, Toolbar, IconButton, Badge, MenuItem, Menu, Typography, Button } from '@material-ui/core';
import { ShoppingCart } from '@material-ui/icons';
import { Link, useLocation } from 'react-router-dom';
import Jaziira from './JAZIIRA.png'
import useStyles from './style'
import './style.css'


const Navigation = () => {
  const [openH, setOpenH] = useState(true)
  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState(null);
  const classes = useStyles();
  const location = useLocation();

  const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);

  const handleMobileMenuClose = () => setMobileMoreAnchorEl(null);


  return (
    <>
      <AppBar position="fixed" className={classes.appBar} color="inherit">
        <Toolbar>
          <Typography component={Link} to="/" variant="h6" className={classes.title} color="inherit">
            <img src={Jaziira}  alt="commerce.js" height="25px" className={classes.image} /> CARWA JAZIIRA
          </Typography>
          <div className={classes.grow} />
           <i class="fa-solid fa-bars" style={{cursor:'pointer'}} onClick={()=>{
            setOpenH(prev=> !prev)
          }}></i>
         
          {location.pathname === '/' && (
          <div className={classes.button}>
            <IconButton component={Link} to="/cart" aria-label="Show cart items" color="inherit">
              <Badge badgeContent={totalItems} color="secondary">
                <ShoppingCart />
              </Badge>
             
            </IconButton>
          </div>
          )}
        </Toolbar>
      </AppBar>
      { openH && (
      <div className='menuHolder' >
        <a component={Link} to='/badeecada'>item 1</a> 
        <a component={Link} to='/badeecada'>item 1</a> 
        <a component={Link} to='/badeecada'>item 1</a> 
      </div>

      )

      }
    </>
  );
};

export default Navigation;
