import React, { useState } from 'react';
import { AppBar, Toolbar, IconButton, Badge, MenuItem, Menu, Typography, Button, Card } from '@material-ui/core';
import { ShoppingCart } from '@material-ui/icons';
import { Link, useLocation } from 'react-router-dom';
import Jaziira from './JAZIIRA.png'
import useStyles from './style'
import './style.css'
import userImage from './userImage.png'
import { api } from '../axiosSetup';
import { useEffect } from 'react';
// import './Functionality'


const Navigation = () => {
  const [open, setOpen] = useState(false)
   function openOrClose(){
   const toggleButton = document.getElementsByClassName('toggle-button')[0]
   const navbarLinks = document.getElementsByClassName('navbar-links')[0]
   console.log(toggleButton)
   toggleButton.addEventListener('click', () => {
     navbarLinks.classList.toggle('active')
   })

 }
  
 const classes = useStyles()
  const [openH, setOpenH] = useState(true)
  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = useState(null);
  const classNamees = useStyles();
  const location = useLocation();
  const [profile, setProfile] = useState([])
  

  const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);

  const handleMobileMenuClose = () => setMobileMoreAnchorEl(null);
  

  const [image, setImage] = useState('')


  async function getUserProfile(){
   const data =  await (await api.get('/get/user/profile')).data
   const email = sessionStorage.getItem('email')
   const targetProfile = data.filter(prof=> prof.email == email)
   setImage(targetProfile[0].sawir) 
   
   console.log(targetProfile[0].sawir)
  }
  
  useEffect(()=>{
    getUserProfile()
}, [])
const normlImage = image || userImage
console.log(image)

  return (
    <div >
    <div >
    <nav className="navbar">
    <div className="brand-title">
    <img src={Jaziira}  alt="commerce.js" height="25px" className={classes.image} /> CARWA JAZIIRA
       
        </div>
    <a href="#" className="toggle-button" onClick={()=>{
      openOrClose()
    }}>
      <span className="bar"></span>
      <span className="bar"></span>
      <span className="bar"></span>
    </a>
    <div className="navbar-links">
      <ul>
        <Link to='/home' style={{textDecoration:'none'}}>
        <li ><a href="#">Home</a></li>
        </Link> 
        <Link to='/badeeco' style={{textDecoration:'none'}}>
        <li ><a href="#">Cunooyinka</a></li> 
        </Link> 
        <Link to='/history' style={{textDecoration:'none'}}>
        <li ><a href="#">XOGTA DALABYADA</a></li> 
        </Link> 
        {
          sessionStorage.getItem('role') == 'admin' && (
        <Link to='/panel' style={{textDecoration:'none'}}>
        <li ><a href="#">ADMIN PANEL</a></li> 
        </Link> 
          )
        }
       
        
      </ul>
    </div>
  </nav>
    </div>
    <a href="#" onClick={()=>{
      setOpen(prev=> !prev)
    }}>
     
        <img src={normlImage} style={{width:'50px', height:"50px", position:'absolute', right:'70px', top:'4px', borderRadius:'50%', cursor:'pointer'}} alt="user image" />

      
    </a>
    {open &&
    <form  action="http://localhost:2000/api/v1/post/user/profile">
    <Card style={{minWidth:'300px', padding:"10px", minHeight:'100px', position:'absolute', right:'60px', top:'70px'}}>
      <Typography variant='h6' align='left'>signed in as <b>Yusuf Shire</b> </Typography>
       <label style={{cursor:'pointer'}} htmlFor="userImage"><i typeof='file' class="fa-solid fa-upload" onClick={()=> readFile()}  style={{fontSize:'40px', margin:'20px 10px', cursor:'pointer'}}></i>SAWIR SOO GALI</label>
       <input name='profile' onChange={postUserPhoto} type="file" id='userImage'  style={{display:'none'}} /><br />
        <Button component={Link} variant='contained' color='secondary' to='/' >LOGOUT</Button>
        
    </Card>
    <img src= {image} alt="profile image" style={{width:'300px', height:'300px',position:'absolute', top:"200px"}} />

    </form>
 }

    </div>
  );
};

export default Navigation;
