import React, { useState, useEffect } from 'react'
import { TextField, Typography, Grid, Button } from '@material-ui/core'
import { Card, CardMedia, CardContent, CardActions, IconButton, Paper } from '@material-ui/core';
import DeleteIcon from '@material-ui/icons/Delete'
import Navbar from '../../COMPONENTS/Navbar/Navbar';
import { api } from '../../COMPONENTS/axiosSetup'
import EditIcon from '@material-ui/icons/Edit';
import { Edit } from '@material-ui/icons';
function MaamulBadeeco() {
    function importAll(r) {
        let images = {};
        r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
        return images;
      }
      const [q, setQ] = useState(0)
      
      
      const images = importAll(require.context('../../IMAGES', false, /\.(png|jpe?g|svg)$/));
    const [products, setProducts] = useState([])
    async function getProducts(){
      const data =   await (await api.get('/get/maamul/badeeco')).data
      setProducts(data)
      console.log(data)
    }
    useEffect(()=>{
        getProducts()
    }, [])
   const [showForm, setShowForm] = useState(false)
    function EditForm(id){
        console.log(id)
      return (
        <>
        <input type="text" style={{width:'200px', height:'40px', borderRadius:'20px', padding:'9px'}} />
        <input type="text" />
        </>
      )
    }
    function NormalState({ product }){
        return (
            <Grid item xs={12} sm={12 } style={{display:"flex", padding:'10px', justifyContent:'center'}}>
            <Card style={{display:"flex", justifyContent:'space-around', alignItems:'center', padding:'5px', minWidth:'480px'}}>
            <img src={images[product.image]} alt="" style={{width:"100px", height:'100px', borderRadius:'50%'}} />
            <Typography variant='h5'>{product.magac}</Typography>
            <Typography variant='h4'>${product.price}</Typography>
            <EditIcon style={{cursor:'pointer'}} onClick={()=>{
                setShowForm(true)
            }} />
            </Card>
        </Grid>
        )
    }

  return (
    <>
    <Navbar />
    <div style={{margin:'20px'}}>
      <Grid container align='center' >
        {console.log(products)}
        {products.map(product=>{
            {console.log(product)}
            return (
       !showForm ? <NormalState product={product} /> : <EditForm id={product.id} />
            )
        })}
      </Grid>
    </div>
    </>
  )
}

export default MaamulBadeeco
